import React, { useState } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import ListItem from '../components/ListItem';
import { jobs } from '../data';
import { categories } from '../data';

export default function List() { 
  const [job, setJob] = useState(jobs);
  const [nature, setNature] = useState('');
  const [cat, setCat] = useState(0);
  console.log(cat)
  const [country, setCountry] = useState('');
  const jobCount = job.length;
   const [filter, setFilter] = useState(false);
 
   const handleClick = (e) =>{
    e.preventDefault();
    if(filter === false){
      setFilter(true)
    }else{
      setFilter(false)
    }
   }

   const handleCatChange = (e) =>{
    e.preventDefault();
    setCat(e.target.value);
     console.log(cat);
    const newJobs = jobs.filter((item) => item.category === Number(cat));
    setJob(newJobs);
   }
   const handleCoungtryChange = (e) => {
     e.preventDefault();
     setCountry(e.target.value);
     const newJobs = jobs.filter((item) => item.country === String(country));
     setJob(newJobs);
   };
    const handleNatureChange = (e) => {
      e.preventDefault();
      setNature(e.target.value);
      const newJobs = jobs.filter((item) => item.nature === String(nature));
      setJob(newJobs);
    };
  return (
    <div className="container-xxl bg-white p-0">
      <Header />
      <div className="container-xxl py-5 bg-dark page-header mb-5">
        <div className="container my-5 pt-5 pb-4">
          <h1 className="display-3 text-white mb-3 animated slideInDown">
            Job List
          </h1>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb text-uppercase">
              <li className="breadcrumb-item">
                <a href="/">Home</a>
              </li>
              <li className="breadcrumb-item">
                <a href="#">Pages</a>
              </li>
              <li
                className="breadcrumb-item text-white active"
                aria-current="page"
              >
                Job List
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className="filter-container">
        <div className="filter-item">
          <div className="d-md-flex align-items-md-center">
            <div className="h3">Job Listing</div>
            <div className="ml-auto d-flex align-items-center views">
              <span className="btn">
                {" "}
                <span className="fas fa-list-ul"></span>
                <span className="px-md-2 px-1">List view</span>{" "}
              </span>{" "}
              <span className="green-label px-md-2 px-1">{jobCount}</span>{" "}
              <span className="text-muted">Jobs</span>{" "}
            </div>
          </div>
          <div className="filters">
            {" "}
            <button
              className="btn btn-success"
              type="button"
              onClick={handleClick}
            >
              Filters<span className="px-1 fas fa-filter"></span>
            </button>{" "}
          </div>

          <div>
            <div className="py-3">
              <h5 style={{fontSize}}>Filter by Categories</h5>
              <div className="col-md-4">
                <select
                  className="form-select border-0"
                  value={cat}
                  onChange={handleCatChange}
                >
                  <option value="">Select Category</option>
                  {categories.map((item) => (
                    <option value={item.id}>{item.name}</option>
                  ))}
                </select>
              </div>
            </div>
            <div className="py-3">
              <h5 className="font-weight-bold">Filter by Nature</h5>
              <div className="col-md-4">
                <select
                  className="form-select border-0"
                  value={nature}
                  onChange={handleNatureChange}
                >
                  <option value="Full-Time">Full-Time</option>
                  <option value="Part-Time">Part-Time</option>
                </select>
              </div>
            </div>
            <div className="py-3">
              <h5 className="font-weight-bold">Filter by Location</h5>
              <div className="col-md-4">
                <select
                  className="form-select border-0"
                  value={country}
                  onChange={handleCoungtryChange}
                >
                  <option value="">Select Location</option>
                  {jobs.map((value) => (
                    <option value={value.country}>{value.country}</option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        </div>
        <div className="list-job"></div>
      </div>
      <Footer />
      <a href="#" class="btn btn-lg btn-primary btn-lg-square back-to-top">
        <i class="bi bi-arrow-up"></i>
      </a>
    </div>
  );
}
