import React, { useState } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import ListItem from '../components/ListItem';
import { jobs } from '../data';
import { categories } from '../data';

export default function List() { 
  const [job, setJob] = useState(jobs);
  const [nature, setNature] = useState('');
  const [cat, setCat] = useState(0);
  console.log(cat)
  const [country, setCountry] = useState('');
  const jobCount = job.length;
   const [filter, setFilter] = useState(false);
 
   const handleClick = (e) =>{
    e.preventDefault();
    if(filter === false){
      setFilter(true)
    }else{
      setFilter(false)
    }
   }

   const handleCatChange = (e) =>{
    e.preventDefault();
    setCat(e.target.value);
     console.log(cat);
    const newJobs = jobs.filter((item) => item.category === Number(cat));
    setJob(newJobs);
   }
   const handleCoungtryChange = (e) => {
     e.preventDefault();
     setCountry(e.target.value);
     const newJobs = jobs.filter((item) => item.country === String(country));
     setJob(newJobs);
   };
    const handleNatureChange = (e) => {
      e.preventDefault();
      setNature(e.target.value);
      const newJobs = jobs.filter((item) => item.nature === String(nature));
      setJob(newJobs);
    };
  return (
    <>
      <div className="container-xxl py-5">
        <div className="container">
          <h1 className="text-center mb-5 wow fadeInUp" data-wow-delay="0.1s">
            Job Listing
          </h1>
          <div
            className="tab-className text-center wow fadeInUp"
            data-wow-delay="0.3s"
          >
           
            <div className="tab-content">
              <div id="tab-1" className="tab-pane fade show p-0 active">
                {job
                  .filter((item) => item.nature === "Part-Time")
                  .map((value, key) => (
                    <RecentJobItem
                      id={value.id}
                      title={value.title}
                      location={value.location}
                      salary={value.salary}
                      nature={value.nature}
                      dataline={value.dateline}
                    />
                  ))}

                <a className="btn btn-primary py-3 px-5" href="/list">
                  Browse More Jobs
                </a>
              </div>
              <div id="tab-2" className="tab-pane fade show p-0">
                {job
                  .filter((item) => item.nature === "Full-Time")
                  .map((value, key) => (
                    <FullTimeItem
                      id={value.id}
                      title={value.title}
                      location={value.location}
                      salary={value.salary}
                      nature={value.nature}
                      dataline={value.dateline}
                    />
                  ))}

                <a className="btn btn-primary py-3 px-5" href="/list">
                  Browse More Jobs
                </a>
              </div>
              <div id="tab-3" className="tab-pane fade show p-0">
                {job
                  .filter((item) => item.nature === "Part-Time")
                  .map((value, key) => (
                    <PartTimeItem
                      id={value.id}
                      title={value.title}
                      location={value.location}
                      salary={value.salary}
                      nature={value.nature}
                      dataline={value.dateline}
                    />
                  ))}

                <a className="btn btn-primary py-3 px-5" href="/list">
                  Browse More Jobs
                </a>
              </div>
            </div>
          </div>
        </div>
        <a href="#" class="btn btn-lg btn-primary btn-lg-square back-to-top">
          <i class="bi bi-arrow-up"></i>
        </a>
      </div>
    </>
  );
}
