import React, { useState } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import ListItem from '../components/ListItem';
import { jobs } from '../data';
import './list.css'

export default function List() { 
  const [job, setJob] = useState(jobs);
   const [filter, setFilter] = useState(false);
 
   const handleClick = (e) =>{
    e.preventDefault();
    if(filter === false){
      setFilter(true)
    }else{
      setFilter(false)
    }
   }
  return (
    <>
      <Header />
      <div className="container-xxl py-5 bg-white">
        <div className="wrapper">
          <div className="d-md-flex align-items-md-center">
            <div className="h3">Job Listing</div>
            <div className="ml-auto d-flex align-items-center views">
              <span className="btn">
                {" "}
                <span className="fas fa-list-ul"></span>
                <span className="px-md-2 px-1">List view</span>{" "}
              </span>{" "}
              <span className="green-label px-md-2 px-1">428</span>{" "}
              <span className="text-muted">Jobs</span>{" "}
            </div>
          </div>

          <div className="filters">
            {" "}
            <button
              className="btn btn-success"
              type="button"
              data-toggle="collapse"
              data-target="#mobile-filter"
              aria-expanded="true"
              aria-controls="mobile-filter"
              onClick={handleClick}
            >
              Filters<span className="px-1 fas fa-filter"></span>
            </button>{" "}
          </div>
          {}
          <div className="content py-md-0 py-3">
            <section id="sidebar">
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Categories</h5>
                <div className="col-md-4">
                  <select className="form-select border-0">
                    <option value="1">Category-2</option>
                    <option value="2">Category-3</option>
                    <option value="3">Category-4</option>
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Nature</h5>
                <div className="col-md-4">
                  <select className="form-select border-0">
                    <option value="1">Full-Time</option>
                    <option value="2">Part-Time</option>
                    <option value="3">Recent Jobs</option>
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Location</h5>
                <div className="col-md-4">
                  <select className="form-select border-0">
                    <option value="1">USA</option>
                    <option value="2">Canada</option>
                    <option value="3">Cameroon</option>
                  </select>
                </div>
              </div>
            </section>
            <section id="job-list">
              <div className="container py-3">
                {job.map((value, key) => (
                  <ListItem
                    id={value.id}
                    title={value.title}
                    location={value.location}
                    salary={value.salary}
                    nature={value.nature}
                    dataline={value.dateline}
                  />
                ))}
              </div>
            </section>
          </div>
        </div>
      </div>
    </>
  );
}
