import React, { useState } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import ListItem from '../components/ListItem';
import { jobs } from '../data';
import './list.css'
import { categories } from '../data';

export default function List() { 
  const [job, setJob] = useState(jobs);
  const [nature, setNature] = useState('');
  const [cat, setCat] = useState('');
  const [country, setCountry] = useState('');
  const jobCount = job.length;
   const [filter, setFilter] = useState(false);
 
   const handleClick = (e) =>{
    e.preventDefault();
    if(filter === false){
      setFilter(true)
    }else{
      setFilter(false)
    }
   }

   const handleCatChange = (e) =>{
    e.preventDefault();
    setCat(e.target.value);
    const newJobs = jobs.filter((item) => item.category === Number(cat));
    setJob(newJobs);
   }
   const handleCoungtryChange = (e) => {
     e.preventDefault();
     setCountry(e.target.value);
     const newJobs = jobs.filter((item) => item.country === String(country));
     setJob(newJobs);
   };
  return (
    <>
      <Header />
      <div className="container-xxl py-5 bg-white">
        <div className="wrapper">
          <div className="d-md-flex align-items-md-center">
            <div className="h3">Job Listing</div>
            <div className="ml-auto d-flex align-items-center views">
              <span className="btn">
                {" "}
                <span className="fas fa-list-ul"></span>
                <span className="px-md-2 px-1">List view</span>{" "}
              </span>{" "}
              <span className="green-label px-md-2 px-1">{jobCount}</span>{" "}
              <span className="text-muted">Jobs</span>{" "}
            </div>
          </div>

          <div className="filters">
            {" "}
            <button
              className="btn btn-success"
              type="button"
              data-toggle="collapse"
              data-target="#mobile-filter"
              aria-expanded="true"
              aria-controls="mobile-filter"
              onClick={handleClick}
            >
              Filters<span className="px-1 fas fa-filter"></span>
            </button>{" "}
          </div>
          {filter === true ? (
            <div id="mobile-filter">
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Categories</h5>
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    value={cat}
                    onChange={handleCatChange}
                  >
                    {categories.map((item, key) => (
                      <option value={item.id}>{item.name}</option>
                    ))}
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Nature</h5>
                <div className="col-md-4">
                  <select className="form-select border-0" value={nature}>
                    <option value="Full-Time">Full-Time</option>
                    <option value="Part-Time">Part-Time</option>
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Location</h5>
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    value={country}
                    onChange={handleCoungtryChange}
                  >
                    
                    {jobs.map((value, key) => (
                      <option value={value.country}>{value.country}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
          ) : (
            <p></p>
          )}
          <div className="content py-md-0 py-3 bg-white">
            <section id="sidebar">
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Categories</h5>
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    value={cat}
                    onChange={handleCatChange}
                  >
                    {categories.map((item, key) => (
                      <option value={item.id}>{item.name}</option>
                    ))}
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Nature</h5>
                <div className="col-md-4">
                  <select className="form-select border-0" value={nature}>
                    <option value="Full-Time">Full-Time</option>
                    <option value="Part-Time">Part-Time</option>
                  </select>
                </div>
              </div>
              <div className="py-3">
                <h5 className="font-weight-bold">Filter by Location</h5>
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    value={country}
                    onChange={handleCoungtryChange}
                  >
                    {jobs.map((value, key) => (
                      <option value={value.country}>{value.country}</option>
                    ))}
                  </select>
                </div>
              </div>
            </section>
            <section id="job-list">
              <div className="container py-3 bg-white">
                {job.map((value, key) => (
                  <ListItem
                    id={value.id}
                    title={value.title}
                    location={value.location}
                    salary={value.salary}
                    nature={value.nature}
                    dataline={value.dateline}
                  />
                ))}
              </div>
            </section>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
