import React, { useContext, useMemo, useState } from "react";
import { Helmet } from "react-helmet-async";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "../../axios";
import { Store } from "../../Store";
import Footer from "./Footer";
import Nav from "./Nav";
import Sidebar from "./Sidebar";
import countryList from "react-select-country-list";
export const Newjob = () => {
  const { state } = useContext(Store);
  const { user } = state;
  const date = new Date();
  const currendate = `${date.getDay()}/${date.getMonth()+1}/${date.getFullYear()}`;
  const [tittle, setTittle] = useState("");
  const [values, setValues] = useState("");
  const [location, setLocation] = useState("");
  const [nature, setNature] = useState("");
  const [dateline, setDateLine] = useState(currendate);
  const [qualification, setQualification] = useState("");
  const [salary, setSalary] = useState("1000-2000");
  const [description, setDescription] = useState("");
  const [companyInfo, setCompanyInfo] = useState("");
  const [publish, setPublish] = useState(currendate);
  const [vacant, setVacant] = useState(0);
  const [country, setCountry] = useState("");
  const [category, setCategory] = useState("");
  const [email, setEmail] = useState("");
const options = useMemo(countryList().g)
  const navigate = useNavigate();
  const addCat = async (e) => {
    e.preventDefault();
    if(!tittle){
        toast.success('Category name is required!')
        return;
    }
    try {
        await axios.post("/category/cats/add", {
      tittle,
    });
     toast.success("Category save successfully");
        
    } catch (error) {
        console.log(error.message)
        toast.success('Error Saving Category Try again!')
    }
    
  };
  return (
    <div>
      <ToastContainer />
      <Helmet>
        <title>New jobs</title>
      </Helmet>
      <Nav />
      <Sidebar />
      <main id="main" className="main">
        <div className="pagetitle">
          <h1>TheEmployer</h1>
          <nav>
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <a href="/">Home</a>
              </li>
              <li className="breadcrumb-item active">Dashboard</li>
            </ol>
          </nav>
        </div>

        <section className="section dashboard">
          <div>
            <h1>New Job</h1>
            <form
              name="form"
              className="addCatForm"
              encType="multipart/form-data"
              method="POST"
              onSubmit={addCat}
            >
              <div className="form-group">
                <label>Tittle</label>
                <input
                  type="text"
                  name="cat"
                  onChange={(e) => setTittle(e.target.value)}
                  placeholder="Software Engineering"
                  value={tittle}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="Newyork - USA"
                  value={location}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Salary Range</label>
                <input
                  type="text"
                  name="salary"
                  onChange={(e) => setSalary(e.target.value)}
                  placeholder="salary"
                  value={salary}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Dateline</label>
                <input
                  type="date"
                  name="dateline"
                  onChange={(e) => setDateLine(e.target.value)}
                  placeholder={currendate}
                  value={dateline}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Vacant Positions</label>
                <input
                  type="number"
                  name="vacant"
                  onChange={(e) => setVacant(e.target.value)}
                  placeholder="salary"
                  value={vacant}
                  className="form-control"
                />
              </div>
              <div className="form-group">
                <label>Company Email</label>
                <input
                  type="email"
                  name="email"
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="email"
                  value={email}
                  className="form-control"
                />
              </div>
              <div className="input-group my-2">
                <div className="input-group-prepend">
                  <label className="input-group-text" for="inputGroupSelect01">
                    Nature
                  </label>
                </div>
                <select
                  className="custom-select"
                  style={{ width: "157px" }}
                  id="inputGroupSelect01"
                  onChange={(e) => setNature(e.target.value)}
                  value={nature}
                >
                  <option selected>Choose...</option>
                  <option value="Full-Time">Full-Time</option>
                  <option value="Partime">Part-Time</option>
                </select>
              </div>
              <div className="input-group my-2">
                <div className="input-group-prepend">
                  <label className="input-group-text" for="inputGroupSelect01">
                    Country
                  </label>
                </div>
                <select
                  className="custom-select"
                  style={{ width: "152px" }}
                  id="inputGroupSelect01"
                  onChange={(e) => setCountry(e.target.value)}
                  value={country}
                >
                  <option selected>Choose...</option>
                  <option value="USA">USA</option>
                  <option value="Turkey">Turkey</option>
                </select>
              </div>
              <div className="input-group my-2">
                <div className="input-group-prepend">
                  <label className="input-group-text" for="inputGroupSelect01">
                    Category
                  </label>
                </div>
                <select
                  className="custom-select"
                  style={{ width: "143px" }}
                  id="inputGroupSelect01"
                  onChange={(e) => setCategory(e.target.value)}
                  value={category}
                >
                  <option selected>Choose...</option>
                  <option value="Marketing">Marketing</option>
                  <option value="Marketing">Human Resource</option>
                </select>
              </div>

              <button className="btn btn-primary my-2" type="submit">
                Create Job
              </button>
            </form>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
};
