import React, { useState } from 'react'
import courosel_1 from '../img/carousel-1.jpg'
import courosel_2 from "../img/carousel-2.jpg";
import { categories } from '../data';
import { jobs } from '../data';
import sec from "../img/sec.jpg";
export default function HeaderCarousel() {
  const [location, setLocation] = useState('');
  const [cat, setCat] = useState(null);
  const CategoryInput = (e) =>{
    e.preventDefault();
    setCat(e.target.value);
  }
   const LocationInput = (e) => {
     e.preventDefault();
     setLocation(e.target.value);
   };

  return (
    <>
      <div className="container-fluid p-0 hero">
        <div className="position-relative">
          <img className="img-fluid" src={courosel_2} alt="carousel" />
          <div
            className="position-absolute top-0 start-0 w-100 h-100 d-flex align-items-center"
            style={{ background: "rgba(43, 57, 64, .5)" }}
          >
            <div className="container">
              <div className="row justify-content-start">
                <div className="col-10 col-lg-8">
                  <h1 className="display-3 text-white animated slideInDown mb-4">
                    Find The Best Startup Job That Fit You
                  </h1>

                  <a
                    href=""
                    className="btn btn-primary py-md-3 px-md-5 me-3 animated slideInLeft"
                  >
                    Search A Job
                  </a>
                  <a
                    href=""
                    className="btn btn-secondary py-md-3 px-md-5 animated slideInRight"
                  >
                    Find A Talent
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div
        className="container-fluid bg-primary mb-5 wow fadeIn"
        data-wow-delay="0.1s"
        style={{ padding: "35px" }}
      >
        <div className="container">
          <div className="row g-2">
            <div className="col-md-10">
              <div className="row g-2">
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    onChange={CategoryInput}
                    value={ca}
                  >
                    <option>Select Category</option>
                    {categories.map((item, key) => (
                      <option value={item.id}>{item.name}</option>
                    ))}
                  </select>
                </div>
                <div className="col-md-4">
                  <select
                    className="form-select border-0"
                    onChange={LocationInput}
                    value={location}
                  >
                    <option>Select Location</option>
                    {jobs.map((item, key) => (
                      <option value={item.id}>{item.location}</option>
                    ))}
                  </select>
                </div>
              </div>
            </div>
            <div className="col-md-2">
              <button className="btn btn-dark border-0 w-100">Search</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
