import React, { useState } from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'
import ListItem from '../components/ListItem';
import { jobs } from '../data';
import { categories } from '../data';

export default function List() { 
  const [job, setJob] = useState(jobs);
  const [nature, setNature] = useState('');
  const [cat, setCat] = useState(0);
  console.log(cat)
  const [country, setCountry] = useState('');
  const jobCount = job.length;
   const [filter, setFilter] = useState(false);
 
   const handleClick = (e) =>{
    e.preventDefault();
    if(filter === false){
      setFilter(true)
    }else{
      setFilter(false)
    }
   }

   const handleCatChange = (e) =>{
    e.preventDefault();
    setCat(e.target.value);
     console.log(cat);
    const newJobs = jobs.filter((item) => item.category === Number(cat));
    setJob(newJobs);
   }
   const handleCoungtryChange = (e) => {
     e.preventDefault();
     setCountry(e.target.value);
     const newJobs = jobs.filter((item) => item.country === String(country));
     setJob(newJobs);
   };
    const handleNatureChange = (e) => {
      e.preventDefault();
      setNature(e.target.value);
      const newJobs = jobs.filter((item) => item.nature === String(nature));
      setJob(newJobs);
    };
  return (
    <div className="container-xxl bg-white p-0">
      <Header />
      <div className="container-xxl py-5 bg-dark page-header mb-5">
        <div className="container my-5 pt-5 pb-4">
          <h1 className="display-3 text-white mb-3 animated slideInDown">
            Job List
          </h1>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb text-uppercase">
              <li className="breadcrumb-item">
                <a href="/">Home</a>
              </li>
              <li className="breadcrumb-item">
                <a href="#">Pages</a>
              </li>
              <li
                className="breadcrumb-item text-white active"
                aria-current="page"
              >
                Job List
              </li>
            </ol>
          </nav>
        </div>
      </div>

      <div className='filter-container'>
        <div></div>
        
      </div>
      <Footer />
      <a href="#" class="btn btn-lg btn-primary btn-lg-square back-to-top">
        <i class="bi bi-arrow-up"></i>
      </a>
    </div>
  );
}
